      <body>
        <div class="header">
          <img src="/McSMS/assets/logo.png" alt="School Logo" class="logo" onerror="this.style.display='none'">
          <div class="school-name">McSMS School Management System</div>
          <div class="school-details">
            Address: School Address Here | Phone: +233 XXX XXX XXX | Email: info@school.com
          </div>
        </div>

        <div class="pay-slip-title">SALARY SLIP</div>

        <div class="employee-info">
          <div class="info-row">
            <span class="label">Employee Name:</span>
            <span class="value">${K.employee_name}</span>
          </div>
          <div class="info-row">
            <span class="label">Employee Number:</span>
            <span class="value">${K.employee_number}</span>
          </div>
          <div class="info-row">
            <span class="label">Designation:</span>
            <span class="value">${K.designation}</span>
          </div>
          <div class="info-row">
            <span class="label">Department:</span>
            <span class="value">${K.department_name||"N/A"}</span>
          </div>
          <div class="info-row">
            <span class="label">Pay Period:</span>
            <span class="value">${K.month}</span>
          </div>
        </div>

        <div class="salary-section">
          <div class="section-title">Basic Salary</div>
          <table>
            <tr>
              <td>Basic Salary</td>
              <td style="text-align: right;">GHS ${parseFloat(K.basic_salary).toFixed(2)}</td>
            </tr>
          </table>

          <div class="section-title earnings">Earnings</div>
          <table>
            ${K.earnings&&K.earnings.length>0?K.earnings.map(dr=>`
                <tr>
                  <td>${dr.component_name}</td>
                  <td style="text-align: right;">GHS ${parseFloat(dr.amount).toFixed(2)}</td>
                </tr>
              `).join(""):'<tr><td colspan="2" style="text-align: center; color: #9ca3af;">No additional earnings</td></tr>'}
            <tr class="total-row">
              <td>Total Earnings</td>
              <td style="text-align: right;">GHS ${parseFloat(K.total_earnings).toFixed(2)}</td>
            </tr>
          </table>

          <div class="section-title deductions">Deductions</div>
          <table>
            ${K.deductions&&K.deductions.length>0?K.deductions.map(dr=>`
                <tr>
                  <td>${dr.component_name}</td>
                  <td style="text-align: right;">GHS ${parseFloat(dr.amount).toFixed(2)}</td>
                </tr>
              `).join(""):'<tr><td colspan="2" style="text-align: center; color: #9ca3af;">No deductions</td></tr>'}
            <tr class="total-row">
              <td>Total Deductions</td>
              <td style="text-align: right;">GHS ${parseFloat(K.total_deductions).toFixed(2)}</td>
            </tr>
          </table>
        </div>

        <div class="net-salary">
          <div class="net-salary-label">NET SALARY</div>
          <div class="net-salary-amount">GHS ${parseFloat(K.net_salary).toFixed(2)}</div>
        </div>

        <div class="footer">
          <p>This is a computer-generated pay slip and does not require a signature.</p>
          <p>Generated on ${new Date().toLocaleDateString()} at ${new Date().toLocaleTimeString()}</p>
        </div>

        <button class="print-button" onclick="window.print()">Print Pay Slip</button>
      </body>
      </html>
